document.addEventListener('DOMContentLoaded', function() {
    const menuButtons = document.querySelectorAll('.menu-btn');
    const menuItemsContainer = document.getElementById('menu-items');
    const featuredDishes = document.getElementById('featured-dishes');

    const menuData = {
        entradas: [
            { name: "Causa Rellena", description: "Papa amarilla rellena con pollo o atún.", img: "causa.jpg" },
            { name: "Ají de Gallina", description: "Gallina desmenuzada en salsa de ají amarillo.", img: "aji.jpg" }
        ],
        principales: [
            { name: "Lomo Saltado", description: "Carne de res salteada con cebolla y tomate.", img: "lomo.jpg" },
            { name: "Ají de Gallina", description: "Gallina desmenuzada en salsa de ají amarillo.", img: "aji.jpg" }
        ],
        postres: [
            { name: "Mazamorra Morada", description: "Postre a base de maíz morado.", img: "mazamorra.jpg" },
            { name: "Arroz con Leche", description: "Clásico postre con arroz y leche condensada.", img: "arroz.jpg" }
        ],
        bebidas: [
            { name: "Chicha Morada", description: "Bebida de maíz morado.", img: "chicha.jpg" },
            { name: "Pisco Sour", description: "Cóctel clásico peruano a base de pisco.", img: "pisco.jpg" }
        ]
    };

    function displayMenuItems(category) {
        if (featuredDishes) {
            featuredDishes.classList.add('hidden');
        }

        menuItemsContainer.innerHTML = '';

        const items = menuData[category];
        if (items) {
            items.forEach(function(item) {
                const itemElement = document.createElement('div');
                itemElement.classList.add('menu-item');
                itemElement.innerHTML = `
                    <img src="${item.img}" alt="${item.name}">
                    <h3>${item.name}</h3>
                    <p>${item.description}</p>
                `;
                menuItemsContainer.appendChild(itemElement);

                setTimeout(() => itemElement.classList.add('appear'), 50);
            });
        }
    }

    menuButtons.forEach(function(button) {
        button.addEventListener('click', function() {
            const category = button.getAttribute('data-category');
            displayMenuItems(category);
        });
    });
});